css:
@font-face {
  font-family: Junge;
  src: url(Junge-Regular.ttf);
}

.clearfix:after {
  content: "";
  display: table;
  clear: both;
}

a {
  color: #001028;
  text-decoration: none;
}

body {
  font-family: Junge;
  position: relative;
  width: 21cm;  
  height: 29.7cm; 
  margin: 0 auto; 
  color: #001028;
  background: #FFFFFF; 
  font-size: 14px; 
}

.arrow {
  margin-bottom: 4px;
}

.arrow.back {
  text-align: right;
}

.inner-arrow {
  padding-right: 10px;
  height: 30px;
  display: inline-block;
  background-color: rgb(233, 125, 49);
  text-align: center;

  line-height: 30px;
  vertical-align: middle;
}

.arrow.back .inner-arrow {
  background-color: rgb(233, 217, 49);
  padding-right: 0;
  padding-left: 10px;
}

.arrow:before,
.arrow:after {
  content:'';
  display: inline-block;
  width: 0; height: 0;
  border: 15px solid transparent;
  vertical-align: middle;
}

.arrow:before {
  border-top-color: rgb(233, 125, 49);
  border-bottom-color: rgb(233, 125, 49);
  border-right-color: rgb(233, 125, 49);
}

.arrow.back:before {
  border-top-color: transparent;
  border-bottom-color: transparent;
  border-right-color: rgb(233, 217, 49);
  border-left-color: transparent;
}

.arrow:after {
  border-left-color: rgb(233, 125, 49);
}

.arrow.back:after {
  border-left-color: rgb(233, 217, 49);
  border-top-color: rgb(233, 217, 49);
  border-bottom-color: rgb(233, 217, 49);
  border-right-color: transparent;
}

.arrow span { 
  display: inline-block;
  width: 80px; 
  margin-right: 20px;
  text-align: right; 
}

.arrow.back span { 
  margin-right: 0;
  margin-left: 20px;
  text-align: left; 
}

h1 {
  color: #5D6975;
  font-family: Junge;
  font-size: 2.4em;
  line-height: 1.4em;
  font-weight: normal;
  text-align: center;
  border-top: 1px solid #5D6975;
  border-bottom: 1px solid #5D6975;
  margin: 0 0 2em 0;
}

h1 small { 
  font-size: 0.45em;
  line-height: 1.5em;
  float: left;
} 

h1 small:last-child { 
  float: right;
} 

#project { 
  float: left; 
}

#company { 
  float: right; 
}

table {
  width: 100%;
  border-collapse: collapse;
  border-spacing: 0;
  margin-bottom: 30px;
}

table th,
table td {
  text-align: center;
}

table th {
  padding: 5px 20px;
  color: #5D6975;
  border-bottom: 1px solid #C1CED9;
  white-space: nowrap;        
  font-weight: normal;
}

table .service,
table .desc {
  text-align: left;
}

table td {
  padding: 20px;
  text-align: right;
}

table td.service,
table td.desc {
  vertical-align: top;
}

table td.unit,
table td.qty,
table td.total {
  font-size: 1.2em;
}

table td.sub {
  border-top: 1px solid #C1CED9;
}

table td.grand {
  border-top: 1px solid #5D6975;
}

table tr:nth-child(2n-1) td {
  background: #EEEEEE;
}

table tr:last-child td {
  background: #DDDDDD;
}

#details {
  margin-bottom: 30px;
}

footer {
  color: #5D6975;
  width: 100%;
  height: 30px;
  position: absolute;
  bottom: 0;
  border-top: 1px solid #C1CED9;
  padding: 8px 0;
  text-align: center;
}

html:
{% set company = frappe.get_doc("Company", doc.company)%}
{% if (doc.company_address) %}
    {% set supplier_address_doc = frappe.get_doc('Address', doc.company_address) %}
{% endif %}

{% if(doc.customer_address) %}
    {% set customer_address = frappe.get_doc('Address', doc.customer_address ) %}
{% endif %}

{% if(doc.shipping_address_name) %}
    {% set customer_shipping_address = frappe.get_doc('Address', doc.shipping_address_name ) %}
{% endif %}
<!--Dynamic Colspan for total row columns-->
{% set col = namespace(one = 2, two = 1) %}
{% set length = doc.taxes | length %}
{% set length = length / 2 | round %}
{% set col.one = col.one + length %}
{% set col.two = col.two + length %}

{%- if(doc.taxes | length % 2 > 0 ) -%}
  {% set col.two = col.two + 1 %}
{% endif %}

<!-- Items -->
{% set total = namespace(amount = 0) %}

<div class="company row"> {% if company.company_logo %}<img src="{{company.company_logo}}" style="max-width: 120px;">{%endif%}</div>
<main>
  <h1  class="clearfix"><small><span>DATE</span><br />{{doc.posting_date}}</small>{{doc.name}} <small><span>DUE DATE</span><br /> {{doc.posting_date}}</small></h1>
  <table>
    <thead>
      <tr>
        <th class="service">SERVICE</th>
        <th>PRICE</th>
        <th>QTY</th>
        <th>Taxable Amount</th>

        {% for row in doc.taxes %}
          <th style="min-width: 130px">{{row.description}}</th>
        {% endfor %}
        <th>TOTAL</th>
      </tr>
    </thead>
    <tbody>
      {%- for item in doc.items -%}
      {% set total.amount = item.amount %}
      <tr>
        <td>{{ item.item_name or item.item_code }}</td>
        <td>{{ item.get_formatted("rate") }}</td>
        <td>{{ item.qty }}</td>
        <td>{{ item.get_formatted("amount") }}</td>
         {% for row in doc.taxes %}
              {% set data_object = json.loads(row.item_wise_tax_detail) %}
              {% set key = item.item_code or item.item_name %}
              {% set tax_amount = frappe.utils.flt(data_object[key][1]/doc.conversion_rate, row.precision('tax_amount')) %}
              <td>
                 <div class="qr-flex">
                  {%- if(data_object[key][0])-%}
                  <span>{{ frappe.format(data_object[key][0], {'fieldtype': 'Percent'}) }}</span>
                  {%- endif -%}
                  <span>
                  {%- if(data_object[key][1])-%}
                      {{ frappe.format_value(tax_amount, currency=doc.currency) }}</span>
                      {% set total.amount = total.amount + tax_amount %}
                  {%- endif -%}
                  </div>
              </td>
          {% endfor %}
        <td>{{  frappe.format_value(frappe.utils.flt(total.amount, doc.precision('total_taxes_and_charges')), currency=doc.currency) }}</td>
      </tr>
      {%- endfor -%}
    </tbody>
  </table>
  {% set customer_tax_id = frappe.db.get_value('Customer', doc.customer, 'tax_id') %}
  <div id="details" class="clearfix">
    <div id="project">
      <div class="arrow"><div class="inner-arrow"><span>CLIENT</span> {{ doc.customer }}</div></div>
      {% if(customer_address) %}
      <div class="arrow"><div class="inner-arrow"><span>ADDRESS</span> {{ customer_address.address_line1}}</div></div>
      {%endif%}

      {% if(doc.customer.phone) %}
      <div class="arrow"><div class="inner-arrow"><span>EMAIL</span> {{doc.customer.phone}}</div></div>
      {%endif%}
    </div>
    <div id="company">
      <div class="arrow back"><div class="inner-arrow">{{ company.name }} <span>COMPANY</span></div></div>
      <div class="arrow back"><div class="inner-arrow">{{ company.tax_id }} <span>TAX ID</span></div></div>
      <div class="arrow back"><div class="inner-arrow">{{ company.company_name_in_arabic }} <span>COMPANY</span></div></div>
    </div>
  </div>
  <div id="notices">
    <div>NOTICE:</div>
    <div class="notice">Check your items Good after go out .</div>
  </div>
</main>
<footer>
  Invoice was created on a computer and is valid without the signature and seal.
</footer>
